import { FC } from "react";
import { Button } from "../styled_components/Button";
import { StyledIcon } from "../styled_components/Icon";
import {
  faPause,
  faPlay,
  faCaretDown,
  faPlus,
  faUpRightAndDownLeftFromCenter,
  faMagnifyingGlass,
} from "@fortawesome/free-solid-svg-icons";

interface IPlayAllButtonsProps {
  songList: Sog[];
}
export const PlayAllButtons: FC<IPlayAllButtonsProps> = () => {
    return (
      <div
      style={{
        display: "flex",
        justifyContent: "space-between",
        flexWrap: "wrap",
        padding: "10px 0",
        background: "#d3d3d3b5",
        width: "90%",
        color: "#5a5959",
        textAlign: "start",
        fontWeight: "bold",
        fontSize: "13px",
        fontFamily: "Arial,Helvetica,sans-serif",
      }}
    >
      <div style={{ display: "flex" }}>
        <Button onClick={handlePlayAll} $primary>
          <StyledIcon icon={isPlaying ? faPause : faPlay} $primary />
          Play All
          <StyledIcon icon={faCaretDown} />
        </Button>
        <Button $primary>
          <StyledIcon icon={faPlus} />
          Add All
          <StyledIcon icon={faCaretDown} />
        </Button>
      </div>
      <div style={{ display: "flex" }}>
        <Button $primary onClick={sortSongs}>
          <StyledIcon icon={faUpRightAndDownLeftFromCenter} />
          Track Nu...
          <StyledIcon icon={faCaretDown} />
        </Button>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            position: "relative",
          }}
        >
          <StyledIcon
            icon={faMagnifyingGlass}
            style={{ position: "absolute", right: "5px" }}
          />
          <input
            type="search"
            style={{
              padding: "0.5em 1em",
              borderRadius: "25px",
              border: "1px solid",
            }}
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Search song"
          />
        </div>
      </div>
    </div>
    );
  };
