import { useState, useEffect } from "react";
import "./App.css";
import { SongList } from "./components/SongList";
import { Song } from "./types";
import { MusicUploadForm } from "./components/MusicUploadForm";
import {
  faPause,
  faPlay,
  faCaretDown,
  faPlus,
  faUpRightAndDownLeftFromCenter,
  faMagnifyingGlass,
} from "@fortawesome/free-solid-svg-icons";
import { Button } from "./styled_components/Button";
import { StyledIcon } from "./styled_components/Icon";
import { SongContext } from "./SongContext";

function App() {
  const [songList, setSongList] = useState<Song[]>([
    {
      songName: "Halo",
      artistName: "Blackbird Blackbird",
      trackNumber: 11,
      file: "/music/music1.mp3",
    },
    {
      songName: "Blind",
      artistName: "Blackbird Blackbird",
      trackNumber: 2,
      file: "/music/music2.mp3",
    },
    {
      songName: "Twin Flames",
      artistName: "Blackbird Blackbird",
      trackNumber: 1,
      file: "/music/music3.mp3",
    },
    {
      songName: "Left to Hurt",
      artistName: "Blackbird Blackbird",
      trackNumber: 4,
      file: "/music/music4.mp3",
    },
    {
      songName: "Starlight",
      artistName: "Blackbird Blackbird",
      trackNumber: 5,
      file: "/music/music5.mp3",
    },
    {
      songName: "Modern Disbelief",
      artistName: "Blackbird Blackbird",
      trackNumber: 6,
      file: "/music/music6.mp3",
    },
  ]);
  const [isPlaying, setIsPlaying] = useState<boolean>(false);
  const [sorted, setSorted] = useState<boolean>(true);
  const [inputValue, setInputValue] = useState<string>("");
  const [currentSongIndex, setCurrentSongIndex] = useState(-1);
  const [currentSong, setCurrentSong] = useState<HTMLAudioElement | null>(null);
  const handleTogglePlay = (index: number) => {
    const audioUrl = songList[index]?.file;
    currentSong?.pause();
    if (index == currentSongIndex) {
      setCurrentSongIndex(-1);
    } else if (audioUrl) {
      const audio = new Audio(audioUrl);
      audio.play();
      setCurrentSong(audio);
      setCurrentSongIndex(index);
    }
  };
  const pushItem = (newSong: Song) => {
    setSongList((prevSongLists: Song[]) => [...prevSongLists, newSong]);
  };
  const handlePlayAll = () => {
    if (isPlaying) {
      currentSong?.pause();
      setCurrentSongIndex(-1);
    } else {
      handleTogglePlay(0);
    }
    setIsPlaying(!isPlaying);
  };

  useEffect(() => {
    if (isPlaying) {
      const timer = setInterval(() => {
        console.log(currentSong?.currentTime, currentSong?.duration);
        if (
          currentSong &&
          Number(currentSong?.currentTime) >= Number(currentSong?.duration)
        ) {
          if (currentSongIndex < songList.length - 1) {
            handleTogglePlay(currentSongIndex + 1);
          } else {
            handlePlayAll();
          }
        }
      }, 1000);
      return () => clearInterval(timer);
    }
  });
  const sortSongs = () => {
    setSongList([
      ...songList.sort((a, b) =>
        sorted ? a.trackNumber - b.trackNumber : b.trackNumber - a.trackNumber
      ),
    ]);
    setSorted(!sorted);
  };
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        flexWrap: "wrap",
        alignItems: "center",
      }}
    >
      <SongContext.Provider
        value={{ songList, pushItem, handleTogglePlay, currentSongIndex }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            flexWrap: "wrap",
            padding: "10px 0",
            background: "#d3d3d3b5",
            width: "90%",
            color: "#5a5959",
            textAlign: "start",
            fontWeight: "bold",
            fontSize: "13px",
            fontFamily: "Arial,Helvetica,sans-serif",
          }}
        >
          <div style={{ display: "flex" }}>
            <Button onClick={handlePlayAll} $primary>
              <StyledIcon icon={isPlaying ? faPause : faPlay} $primary />
              Play All
              <StyledIcon icon={faCaretDown} />
            </Button>
            <Button $primary>
              <StyledIcon icon={faPlus} />
              Add All
              <StyledIcon icon={faCaretDown} />
            </Button>
          </div>
          <div style={{ display: "flex" }}>
            <Button $primary onClick={sortSongs}>
              <StyledIcon icon={faUpRightAndDownLeftFromCenter} />
              Track Nu...
              <StyledIcon icon={faCaretDown} />
            </Button>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                position: "relative",
              }}
            >
              <StyledIcon
                icon={faMagnifyingGlass}
                style={{ position: "absolute", right: "5px" }}
              />
              <input
                type="search"
                style={{
                  padding: "0.5em 1em",
                  borderRadius: "25px",
                  border: "1px solid",
                }}
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Search song"
              />
            </div>
          </div>
        </div>
        <SongList inputValue={inputValue} />
        <MusicUploadForm />
      </SongContext.Provider>
    </div>
  );
}

export default App;
